actionConfiguration.body:
function () {
  var totalSeconds = 0;
  var delay = 30000;
  var time = Get_Report.parseTimeStringToMilliseconds(Test_Time.text);
  console.log(time);
  var interval = setInterval(function () {
    console.log("执行代码块，已经过去 " + totalSeconds / 1000 + " 秒");
    if (totalSeconds < time && Evaluate_Process.text.startsWith("Test has begin") || Evaluate_Process.text.startsWith("Test has resumed")) {
      totalSeconds = totalSeconds + delay;
      Get_Report_Query.run();
      var a = Get_Report_Query.data;
      Bug_Report_Plain_Text.setText(a);
      console.log(a);
    } else {
      console.log("Interval to get bug report has stopped!");
      clearInterval(interval);
    }
  }, delay);
}

