function (str) {
  const value = parseInt(str);
  const unit = str[str.length - 1];
  switch (unit) {
    case 'ms':
      return value;
    case 's':
      return value * 1000;
    case 'm':
      return value * 60000;
    case 'h':
      return value * 3600000;
    case 'd':
      return value * 86400000;
    default:
      return NaN;
  }
}

