export default {
	myVar1: [],
	myVar2: {},

	parseTimeStringToMilliseconds(str) {
		// 提取数字部分和单位部分
		const value = parseInt(str);
		const unit = str[str.length - 1];
		// 根据单位计算毫秒数
		switch (unit) {
			case 'ms':
				return value;
			case 's':
				return value * 1000;
			case 'm':
				return value * 60000;
			case 'h':
				return value * 3600000;
			case 'd':
				return value * 86400000;
			default:
				return NaN; // 无法识别的单位
		}
	},
	getReportInterval () {
		//	write code here
		//	this.myVar1 = [1,2,3]

		var totalSeconds = 0;
		var delay = 30000;
		var time = this.parseTimeStringToMilliseconds(Test_Time.text);
		console.log(time);
		var interval = setInterval(function() {
			console.log("执行代码块，已经过去 " + (totalSeconds / 1000) + " 秒");
			// console.log("isRun:" + Evaluate_Process_Shower.isRun);
			if ((totalSeconds < time) && (Evaluate_Process.text.startsWith("Test has begin")) || (Evaluate_Process.text.startsWith("Test has resumed")) ) {
				// 在这里执行您想要执行的代码块
				totalSeconds = totalSeconds + delay;
				Get_Report_Query.run();
				var a = Get_Report_Query.data;
				Bug_Report_Plain_Text.setText(a);
				console.log(a);
			} else {
				console.log("Interval to get bug report has stopped!")
				clearInterval(interval); // 停止定时器
			}
		}, delay);

	},
	getReortOnce () {
		Get_Report_Query.run();
		var a = Get_Report_Query.data;
		Bug_Report_Plain_Text.setText(a);
	},
	async myFun2 () {
		//	use async-await or promises
		//	await storeValue('varName', 'hello world')
	}
}

