		Stop_Replay.run();
		Replay_Process.setText("Reply is stopped!")
	},
	getReplyReport () {
		var totalSeconds = 0;
		var delay = 30000;
		var time = 600000;
		var interval = setInterval(function() {
			console.log("执行获取重放报告代码块，已经过去 " + (totalSeconds / 1000) + " 秒");
