				Replay_Report_Text.setText(a);
				console.log(a);
			} else {
				console.log("Interval to get replay report has stopped!")
				clearInterval(interval); // 停止定时器
			}
		}, delay);
	},
	async myFun2 () {
		//	use async-await or promises
		//	await storeValue('varName', 'hello world')
	}
}

